mapper.select:
SELECT * FROM (
	SELECT rownum r_num, a.* FROM(
		SELECT * FROM bbs WHERE status = 0 
		AND bname = #{bname} ORDER BY b_idx DESC
	) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:

UPDATE bbs 
SET
	subject = #{subject},
	content = #{content}

		,file_name=#{file_name}

WHERE b_idx = #{b_idx}

UPDATE bbs
SET status = 1
WHERE b_idx= #{b_idx}

UPDATE bbs
SET hit = hit + 1
WHERE b_idx = #{b_idx}
